mapper.update:
update fg_inventory
set stock = #{stock}
where uid = #{uid}
  and status = 1
  and stock is not null
  and uid_id is null

update fg_tags_inventory
set quantity = #{quantity}
where uid = #{uid}

mapper.select:
select count(*) from [HTMES_MES_Main].[dbo].[xTend_MaterialTransactions]
where [PartNumber] = #{pn}
  and [Plant] = #{plant}
  and [batch] = #{batch}
  and [TransactionType] = '315'

mapper.update:
update fg_tos
 set status = 2, quantity = #{quantity}
 where to_no = #{toNo} and status = 5

update fg_tos
set stock = #{stock}
where to_no = #{toNo}
